`,(0,c.jsxDEV)(a.pre,{children:(0,c.jsxDEV)(a.code,{className:"language-js",children:`function solution(bridge_length, weight, truck_weights) {
  let bridge = new Array(bridge_length).fill(0);
  console.log(bridge);
  let time = 0;

  while (bridge.length) {
    bridge.shift();
    if (truck_weights.length) {
      const sumBridge = bridge.reduce((a, b) => a + b, 0);
      if (sumBridge + truck_weights[0] <= weight) {
        bridge.push(truck_weights.shift());
      } else {
        bridge.push(0);
      }
    }

    time++;
  }
  return time;
}
